    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_about_us);
        toolbar = (Toolbar) findViewById(R.id.app_bar);
        if (android.os.Build.VERSION.SDK_INT >= Build.VERSION_CODES.LOLLIPOP) {
            getWindow().setNavigationBarColor(getResources().getColor(R.color.colorPrimaryDark));
        }
        current = "";
        setSupportActionBar(toolbar);
        if (getSupportActionBar() != null)
            getSupportActionBar().setDisplayShowHomeEnabled(true);
        NavigationDrawerFragment drawerFragment = (NavigationDrawerFragment)
                getSupportFragmentManager().findFragmentById(R.id.fragment_navigation_drawer);
        drawerFragment.setUp(R.id.fragment_navigation_drawer, (DrawerLayout) findViewById(R.id.drawer_layout), toolbar, true);

        findViews();

        String s = Locale.getDefault().getDisplayLanguage();

        if (s.equalsIgnoreCase("english")) {
            loadEnglish();
        } else if (s.equalsIgnoreCase("français")) {
            loadFrench();
        } else if (s.equalsIgnoreCase("العربية")) {
            loadArabic();
        }

        overviewText.setText(Html.fromHtml(overviewString));
        visionAndMessageText.setText(Html.fromHtml(visionAndMessageTextString));
        objectives_text.setText(Html.fromHtml(objectives_textString));
        areas_of_interest_text.setText(Html.fromHtml(areas_of_interest_textString));
        chairman_text.setText(Html.fromHtml(chairman_textString));
        advisory_council_text.setText(Html.fromHtml(advisory_council_textString));
        center_associates_text.setText(Html.fromHtml(center_associates_textString));
        services_text.setText(Html.fromHtml(services_textString));


    }


